-- Unified ML Models table
CREATE TABLE IF NOT EXISTS public.ml_models (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    name TEXT UNIQUE NOT NULL,
    description TEXT,
    framework TEXT, -- e.g., 'tensorflow', 'pytorch', 'scikit-learn'
