            NEW.user_id,
            NULL,
            'INSERT',
            'profiles',
            NEW.user_id,
            NULL,
            to_jsonb(NEW),
            NULL,
            NULL
        );
        RETURN NEW;
    ELSIF TG_OP = 'UPDATE' THEN
        PERFORM public.create_audit_log(
            NEW.user_id,
            NULL,
            'UPDATE',
            'profiles',
            NEW.user_id,
            to_jsonb(OLD),
            to_jsonb(NEW),
            NULL,
            NULL
        );
        RETURN NEW;
    ELSIF TG_OP = 'DELETE' THEN
        PERFORM public.create_audit_log(
            OLD.user_id,
            NULL,
            'DELETE',
            'profiles',
            OLD.user_id,
            to_jsonb(OLD),
            NULL,
            NULL,
            NULL
        );
        RETURN OLD;
    END IF;
    RETURN NULL;
END;
