    reason TEXT,
    source TEXT DEFAULT 'sendgrid',
    is_active BOOLEAN DEFAULT true,
    suppressed_at TIMESTAMP WITH TIME ZONE NOT NULL,
    unsuppressed_at TIMESTAMP WITH TIME ZONE,
    unsuppressed_by UUID REFERENCES auth.users(id),
