    company_id UUID REFERENCES public.partner_companies(id) ON DELETE CASCADE NOT NULL,
    employee_verification_id UUID,
    loan_amount NUMERIC NOT NULL CHECK (loan_amount >= 500 AND loan_amount <= 10000),
