    full_name VARCHAR(255),
    first_name VARCHAR(100),
    last_name VARCHAR(100),
    date_of_birth DATE,
    gender VARCHAR(20),
    nationality VARCHAR(100),
    issue_date DATE,
    expiry_date DATE,
    place_of_birth VARCHAR(255),
    address TEXT,
    confidence_score FLOAT
);

