END $$;

-- Create workflow_step_type enum
DO $$ BEGIN
    IF NOT EXISTS (SELECT 1 FROM pg_type WHERE typname = 'workflow_step_type_enum') THEN
        CREATE TYPE workflow_step_type_enum AS ENUM (
            'manual_action',
            'api_call',
            'email_send',
            'sms_send',
            'document_sign',
            'data_entry',
            'approval',
            'conditional',
            'parallel',
            'sequential',
            'ai_task',
