);

-- Model Deployments Table
CREATE TABLE ml_model_deployments (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    version_id UUID NOT NULL REFERENCES ml_model_versions(id) ON DELETE CASCADE,
