            'user_login',
            'user_logout',
            'user_registration',
            'user_profile_update',
            'document_upload',
            'document_view',
            'document_sign',
            'document_workflow_start',
            'document_workflow_update',
            'document_workflow_complete',
            'kyc_workflow_start',
            'kyc_workflow_update',
            'kyc_workflow_complete',
            'payment_transaction',
            'system_config_update',
            'security_alert',
            'data_export',
            'data_import',
            'api_call',
            'error',
            'other'
        );
    END IF;
END $$;

-- Create audit_severity_enum
DO $$ BEGIN
    IF NOT EXISTS (SELECT 1 FROM pg_type WHERE typname = 'audit_severity_enum') THEN
